import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import { throwStatement } from '@babel/types';

const ONE_SECOND = 1000;
const secondsToMinutes = (seconds) => {
    let minutes = seconds / 60;
    let hours = minutes / 60;
    seconds = seconds % 60;
    if (hours > 0)
        return `${Math.round(hours)}:${Math.round(minutes)}:${seconds}`;
    return `${Math.round(minutes)}:${seconds}`;
}


function Exercise(props) {
    return (
        <div className="exercise">
            <div>Name: {props.name}</div>
            <button onClick={props.onClick}>stop/start</button>
            <div>Time {props.left_time}</div>
        </div>

    );
}

class Training extends React.Component {
    constructor(props) {

        super(props);

        let training = { name: "training 1", exercises: [{ name: "exec_1", left_time: 5 }, { name: "exec_2", left_time: 4 }, { name: "exec_3", left_time: 6 }, { name: "exec_4", left_time: 2 }] };
        this.main_timer = setInterval(this.updateTimer.bind(this), ONE_SECOND);
        let total_left_time = 0
        let current_exercise_index = 0
        training.exercises.map(exer => total_left_time += exer.left_time);
        this.state = {
            timer: 10, isWorking: false,
            current_exercise_index: current_exercise_index,
            total_left_time: total_left_time,
            current_exercise_left_time:training.exercises[current_exercise_index].left_time,
            training: training
        }
    }

    stopStart() {
        this.setState({ isWorking: !this.state.isWorking });

    }

    updatedTime() {

        this.setState({ timer: this.state.timer - 1 });
    }

    updateTimer() {
        let left_time = this.state.total_left_time;
        let current_exercise_left_time = this.state.current_exercise_left_time;
        let current_exercise_index = this.state.current_exercise_index;
        if (this.state.isWorking) {
            if (left_time > 1) {
                if (current_exercise_left_time - 1 === 0) {
                    current_exercise_left_time = this.state.training.exercises[current_exercise_index].left_time
                    current_exercise_index += 1;
                    setTimeout(() => { beep2(); }, 0)
                    setTimeout(() => { beep2(); }, 300)
                } else {
                    current_exercise_left_time -= 1
                }

                this.setState({
                    total_left_time: left_time - 1,
                    current_exercise_left_time: current_exercise_left_time,
                    current_exercise_index: current_exercise_index
                });
            } else {
                this.setState({ total_left_time: 0, isWorking: false });
            }
            if (left_time - 1 === 0) {
                setTimeout(() => { beep2(); }, 500)
                setTimeout(() => { beep2(); }, 700)
                setTimeout(() => { beep2(); }, 900)
            }
        }

    }


    render() {
        let current_exercise = this.state.training.exercises[this.state.current_exercise_index];
        return (
            <div>
                <div>{this.state.training.name}</div>
                <div>Training left time: {this.state.total_left_time} </div>
                <Exercise onClick={this.stopStart.bind(this)} name={current_exercise.name} left_time={this.state.current_exercise_left_time} />
                <button onClick={this.updatedTime.bind(this)}>
                </button>
            </div>
        );
    }
}

function beep() {
    var snd = new Audio("data:audio/wav;base64,");
    snd.play();
}
let audioCtx = new (window.AudioContext || window.webkitAudioContext)();
function beep2() {
    var oscillator = audioCtx.createOscillator();
    var gainNode = audioCtx.createGain();

    oscillator.connect(gainNode);
    gainNode.connect(audioCtx.destination);

    gainNode.gain.value = 0.35;
    oscillator.frequency.value = 650;
    oscillator.type = 'square';

    oscillator.start();

    setTimeout(
        function () {
            oscillator.stop();
        },
        100
    );
};


function Square(props) {
    return (
        <button className="square" onClick={props.onClick}>
            {props.value}
        </button>
    );
}



//https://stackoverflow.com/questions/879152/how-do-i-make-javascript-beep/29641185#29641185
class Board extends React.Component {
    renderSquare(i) {
        return (
            <Square
                value={this.props.squares[i]}
                onClick={() => this.props.onClick(i)}
            />
        );
    }

    render() {
        return (
            <div>
                <div className="board-row">
                    {this.renderSquare(0)}
                    {this.renderSquare(1)}
                    {this.renderSquare(2)}
                </div>
                <div className="board-row">
                    {this.renderSquare(3)}
                    {this.renderSquare(4)}
                    {this.renderSquare(5)}
                </div>
                <div className="board-row">
                    {this.renderSquare(6)}
                    {this.renderSquare(7)}
                    {this.renderSquare(8)}
                </div>
                <Training />
            </div>
        );
    }
}

class Game extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            history: [{
                squares: Array(9).fill(null)
            }],
            xIsNext: true,
            stepNumber: 0,
        };
    }

    handleClick(i) {
        const history = this.state.history.slice(0, this.state.stepNumber + 1);
        console.log(history);
        const current = history[history.length - 1];
        const squares = current.squares.slice();
        if (calculateWinner(squares) || squares[i]) {
            return;
        }
        squares[i] = this.state.xIsNext ? 'X' : 'O';
        this.setState({
            history: history.concat([{
                squares: squares,

            }]),
            stepNumber: history.length,
            xIsNext: !this.state.xIsNext,
        });
    }

    jumpTo(step) {
        this.setState({
            stepNumber: step,
            xIsNext: (step % 2) === 0
        });
    }

    render() {
        const history = this.state.history;
        console.log(this.state.stepNumber)
        const current = history[this.state.stepNumber];
        const winner = calculateWinner(current.squares);
        console.log('squares')
        console.log(this.state.stepNumber)
        const moves = history.map((step, move) => {
            const desc = move ?
                'Go to move #' + move :
                'Go to game start';
            return (
                <li key={move}>
                    <button onClick={() => this.jumpTo(move)}>{desc}</button>
                </li>
            );
        });


        let status;
        if (winner) {
            status = 'Winner: ' + winner;
        } else {
            status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O');
        }

        return (
            <div className="game">
                <div className="game-board">
                    <Board
                        squares={current.squares}
                        onClick={(i) => this.handleClick(i)}
                    />
                </div>
                <div className="game-info">
                    <div>{status}</div>
                    <ol>{moves}</ol>
                </div>
            </div>
        );
    }
}

